const{remote,ipcRenderer}=require("electron");window.rpc={getPlatform:()=>process.platform,openDevTool:()=>{const e=remote.getCurrentWindow(),n=e.getBrowserView();if(n)if(n.webContents.isDevToolsOpened())n.webContents.closeDevTools();else{const t=e.getSize()[1]<600?"detach":"bottom";n.webContents.openDevTools({mode:t})}},setAlwaysOnTop:e=>{remote.getCurrentWindow().setAlwaysOnTop(e)},closeWindow:()=>{remote.getCurrentWindow().close()},minimizeWindow:()=>{const e=remote.getCurrentWindow();e.blur(),e.minimize()},maximizeWindow:()=>{const e=remote.getCurrentWindow();"win32"===process.platform?e.emit("win32-maximize"):e.maximize()},unmaximizeWindow:()=>{const e=remote.getCurrentWindow();"win32"===process.platform?e.emit("win32-unmaximize"):e.unmaximize()},webContentsFocus:()=>{remote.getCurrentWindow().webContents.focus()},buildDetachPluginOptionMenu:()=>{ipcRenderer.send("detach.handle","buildDetachPluginOptionMenu")},buildDetachPluginInfoMenu:()=>{ipcRenderer.send("detach.handle","buildDetachPluginInfoMenu")},sendSubInputChangeEvent:e=>{ipcRenderer.send("detach.handle","sendSubInputChangeEvent",e)},sendPluginSomeKeyDownEvent:(e,n)=>{ipcRenderer.send("detach.handle","sendPluginSomeKeyDownEvent",e,n)},setWindowMaximizeEvent:(e,n)=>{const t=remote.getCurrentWindow();"win32"===process.platform?(t.addListener("win32-maximize",e),t.addListener("win32-unmaximize",n)):"linux"===process.platform&&(t.addListener("maximize",e),t.addListener("unmaximize",n))}};