if (process.platform === 'darwin') {
  module.exports = require('./darwin.node')
} else if (process.platform === 'win32') {
  const baseAddon = require(`./win32-${process.arch}.node`)
  if (parseFloat(require('os').release()) >= 6.2) {
    baseAddon.uwp = require(`./win32-uwp-${process.arch}.node`)
  }
  module.exports = baseAddon
} else if (process.platform === 'linux') {
  module.exports = require('./linux.node')
}
